import React, { useEffect, useState } from 'react';
import ChartComponent from './components/ChartComponent';
import MapComponent from './components/MapComponent';
import * as d3 from 'd3';

const App = () => {
    const [selectedMunicipio, setSelectedMunicipio] = useState('Paraná'); // Estado para o município selecionado
    const [municipios, setMunicipios] = useState([]);

    // Carregar a lista de municípios ao montar o componente
    useEffect(() => {
        d3.dsv(';', '/assets/pr_af_area.csv').then(data => {
            // Extrair lista de municípios únicos
            const municipiosList = [...new Set(data.map(row => row.mun))];
            setMunicipios(municipiosList);
        });
    }, []);

    return (
        <div>
            <h1>Dashboard Interativo - Agricultura familiar no estado do Paraná</h1>

            <label htmlFor="municipio-select">Escolha um município:</label>
            <select
                id="municipio-select"
                value={selectedMunicipio}
                onChange={(e) => setSelectedMunicipio(e.target.value)}
            >
                {municipios.map(mun => (
                    <option key={mun} value={mun}>{mun}</option>
                ))}
            </select>

            <MapComponent municipio={selectedMunicipio} />
            <ChartComponent municipio={selectedMunicipio} />
        </div>
    );
};

export default App;
