import React, { useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import municipioGeoJSON from '../data/municipio_escolhaummunicipio.geojson'; // certifique-se de ter configurado corretamente para importar o arquivo .geojson

const MapComponent = () => {
    useEffect(() => {
        const map = L.map('map').setView([-24.0, -51.0], 7);  // Centro do Paraná

        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: '&copy; OpenStreetMap contributors'
        }).addTo(map);

        // Adicionando o arquivo GeoJSON ao mapa
        fetch(municipioGeoJSON)
            .then(response => response.json())
            .then(data => {
                L.geoJSON(data, {
                    style: () => ({
                        color: "#3388ff",
                        weight: 2,
                        fillOpacity: 0.1
                    })
                }).addTo(map);
            });

        return () => {
            map.remove();
        };
    }, []);

    return <div id="map" style={{ height: '100%', width: '100%' }}></div>;
};

export default MapComponent;
